const initialState = {
    cart: {
      items: [],
    },
  };
  
  function rootReducer(state = initialState, action) {
    switch (action.type) {
      case 'ADD_ITEM':
        return { ...state, cart: { items: [...state.cart.items, action.payload] } };
      default:
        return state;
    }
  }
  
  export default rootReducer;
