import React from 'react';
import './HomePage.css';
import coldDrinksPic from "../../assets/0x0-maden-suyu-ve-soda-ayni-sey-mi-1545903147207.jpg";

function HomePage() {
  // Resim URL'lerini sabit değişkenler olarak tanımladık
  const hotDrinksPic = "../../assets/indir.jpg";
  const sodaPic = "../../assets/0x0-maden-suyu-ve-soda-ayni-sey-mi-1545903147207.jpg";
  const waterPic = "../../assets/elmaciksu-cunku-dogal-mineralli.jpg";
  const snacksPic = "../../assets/ekran-goruntusu-2023-12-18-162004_1702905626_YWVGO3.webp";
  const fruitPic = "../../assets/fruit.png";

  return (
    <div>
      <div className="frames-container">
        <div className="frame">
          <a href="/hot-drinks">
            <img src={hotDrinksPic} alt="Sıcak İçecekler" />
          </a>
        </div>
        <div className="frame">
          <a href="/cold-drinks">
            <img src={coldDrinksPic} alt="Soğuk İçeceSkler" />
          </a>
        </div>
        <div className="frame">
          <a href="/soda">
            <img src={sodaPic} alt="Soda" />
          </a>
        </div>
        <div className="frame">
          <a href="/water">
            <img src={waterPic} alt="Su" />
          </a>
        </div>
        <div className="frame">
          <a href="/snacks">
            <img src={snacksPic} alt="Atıştırmalık" />
          </a>
        </div>
        <div className="frame">
          <a href="/fruit">
            <img src={fruitPic} alt="Meyve" />
          </a>
        </div>
      </div>

      <div className="footer-space"></div>
    </div>
  );
}

export default HomePage;
