import { createStore } from 'redux';

// Basit bir reducer örneği
const cartReducer = (state = [], action) => {
  switch (action.type) {
    case 'ADD_ITEM':
      return [...state, action.payload];
    default:
      return state;
  }
};

// Redux store'u oluşturuyoruz
const store = createStore(cartReducer);

export default store;
