import React from 'react';
import './DropdownMenu.css';

const DropdownMenu = ({ type }) => {
  const menuItems = {
    coffee: ['Türk Kahvesi', 'Filtre Kahve'],
    tea: ['Çay'],
    snacks: ['Tuzlu', 'Tatlı'],
    soda: ['Limon', 'Portakal', 'Ananas'],
    herbalTea: ['Kuşburnu', 'Zencefil-Bal', 'Yeşil Çay'],
    fruit: ['Meyve'],
};

return (
  <div className="dropdown-menu">
    {menuItems[type].map((item, index) => (
      <a key={index} href="#">
        {item}
      </a>
    ))}
  </div>
);
};

export default DropdownMenu;
